def matrix_mult(A, B):
    # 矩阵A的列数必须与矩阵B的行数相同才能计算内积
    if len(A[0]) != len(B):
        return None

    # 创建一个空矩阵以保存结果
    result = []
    for i in range(len(A)):
        result.append([0]*len(B[0]))

    # 遍历A的每一行和B的每一列，计算内积并将结果保存到对应位置
    for i in range(len(A)):
        for j in range(len(B[0])):
            for k in range(len(B)):
                result[i][j] += A[i][k] * B[k][j]

    return result

