import sys

def shortest_substring_containing_words(s, words):
    if not s or not words:
        return ''
    word_count = {}
    for word in words:
        if word in word_count:
            word_count[word] += 1
        else:
            word_count[word] = 1
    left, count, shortest_substring = 0, len(words), sys.maxsize
    for right, ch in enumerate(s):
        if ch in word_count:
            word_count[ch] -= 1
            if word_count[ch] >= 0:
                count -= 1
        while count == 0:
            if shortest_substring > right - left + 1:
                shortest_substring = right - left + 1
                start = left
            left_char = s[left]
            left += 1
            if left_char in word_count:
                word_count[left_char] += 1
                if word_count[left_char] > 0:
                    count += 1
    if shortest_substring == sys.maxsize:
        return ''
    else:
        return s[start:start+shortest_substring]

