def can_swap_strings(str1, str2):
    # 长度不同无法互换
    if len(str1) != len(str2):
        return False

    # 相等的字符串无需互换
    if str1 == str2:
        return True

    # 统计两个字符串中各字符的出现次数，
    # 如果出现次数不同，则无法互换
    count1 = {}
    count2 = {}
    for i in range(len(str1)):
        if str1[i] not in count1:
            count1[str1[i]] = 1
        else:
            count1[str1[i]] += 1

        if str2[i] not in count2:
            count2[str2[i]] = 1
        else:
            count2[str2[i]] += 1

    if count1 != count2:
        return False

    # 找到两个字符串中不同的字符个数
    diff_count = 0
    for i in range(len(str1)):
        if str1[i] != str2[i]:
            diff_count += 1

    # 如果不同的字符个数为2，则可以互换
    if diff_count == 2:
        return True

    return False
