```
function searchWeather() {
    // 获取用户输入的城市名称
    var city = document.getElementById("cityInput").value;
    // 使用和风天气API发送请求获取实时天气状况
    var xhr = new XMLHttpRequest();
    xhr.open("GET", "https://free-api.heweather.net/s6/weather/now?location=" + city + "&key=YOUR_API_KEY", true);
    xhr.onreadystatechange = function() {
        if (xhr.readyState == 4) {
            // 请求完成
            if (xhr.status == 200) {
                // 请求成功，解析返回的数据，并在页面上显示
                var data = JSON.parse(xhr.responseText);
                var weather = data.HeWeather6[0].now.cond_txt;
                var temperature = data.HeWeather6[0].now.tmp;
                var windDirection = data.HeWeather6[0].now.wind_dir;
                var windStrength = data.HeWeather6[0].now.wind_sc;
                var humidity = data.HeWeather6[0].now.hum;
                var display = "城市：" + city + "<br>" +
                              "天气：" + weather + "<br>" +
                              "温度：" + temperature + "℃<br>" +
                              "风向：" + windDirection + "<br>" +
                              "风力：" + windStrength + "级<br>" +
                              "相对湿度：" + humidity + "%";
                document.getElementById("weatherDisplay").innerHTML = display;
            } else {
                // 请求失败，提示用户
                alert("查询天气失败！");
            }
        }
    };
    xhr.send();
}
```

