def unique_products(l):
  """
  Given a list (l) of integers, returns the number of 
  unique pairs of numbers that multiply together to 
  produce a target number (n).
  """
  products = []
  for i in l:
    for j in l:
      p = i * j
      if p == n:
        products.append((i, j)) 

  unique_products = []
  for p in products:
    if set(p) not in unique_products:
      unique_products.append(set(p))

