function maxChar(str) {
  let chars = {};
  for(let i=0; i<str.length; i++) {
    const char = str.charAt(i);
    if(chars[char]) {
      chars[char]++;
    } else {
      chars[char] = 1;
    }
  }
  let maxChar = '';
  let maxCount = 0;
  for(let char in chars) {
    if(chars[char] > maxCount) {
      maxChar = char;
      maxCount = chars[char];
    }
  }
  return {char: maxChar, count: maxCount};
}

