pragma solidity ^0.8.0;

contract Voting {

    mapping(address => bool) public voters;
    mapping(string => uint) public votes;
    string[] public candidates;

    constructor(string[] memory _candidates) {
        candidates = _candidates;
    }

    function vote(string memory candidate) public {
        require(voters[msg.sender] == false, "You have already voted.");
        require(validateCandidate(candidate), "Invalid candidate.");
        votes[candidate]++;
        voters[msg.sender] = true;
    }

    function validateCandidate(string memory candidate) internal view returns (bool) {
        for (uint i = 0; i < candidates.length; i++) {
            if (keccak256(abi.encodePacked(candidates[i])) == keccak256(abi.encodePacked(candidate))) {
                return true;
            }
        }
        return false;
    }

    function winner() public view returns (string memory) {
        uint maxVote = 0;
        uint index = 0;
        for (uint i = 0; i < candidates.length; i++) {
            if (votes[candidates[i]] > maxVote) {
                maxVote = votes[candidates[i]];
                index = i;
            }
        }
        return candidates[index];
    }
}

