class Player():

    def __init__(self, maze):
        self.maze = maze
        self.position = maze.start

    def move(self, direction):
        x, y = self.position
        if direction == 'left':
            if y > 0 and self.maze.maze[x][y-1] == 1:
                self.position = (x, y-1)
                return True
        elif direction == 'right':
            if y < self.maze.size-1 and self.maze.maze[x][y+1] == 1:
                self.position = (x, y+1)
                return True
        elif direction == 'up':
            if x > 0 and self.maze.maze[x-1][y] == 1:
                self.position = (x-1, y)
                return True
        elif direction == 'down':
            if x < self.maze.size-1 and self.maze.maze[x+1][y] == 1:
                self.position = (x+1, y)
                return True
        return False

    def is_at_end(self):
        return self.position == self.maze.end

