def search_docs(keyword):
    results = []
    for filename in os.listdir(doc_dir):
        if filename.endswith(".txt"):
            with open(os.path.join(doc_dir, filename), "r") as f:
                content = f.read()
                if keyword in content:
                    results.append(filename)
    return results

while True:
