// 获取表单元素
const form = document.querySelector('#myForm');
const nameInput = document.querySelector('#name');
const emailInput = document.querySelector('#email');
const codeInput = document.querySelector('#code');
const codeImg = document.querySelector('#codeImg');
const refreshCodeBtn = document.querySelector('#refreshCode');

// 定义验证码图片路径数组
const codeImgs = [
  'code1.jpg',
  'code2.jpg',
  'code3.jpg',
  'code4.jpg',
  'code5.jpg',
];

// 随机获取验证码图片路径
function getCodeImgSrc() {
  const randomIndex = Math.floor(Math.random() * codeImgs.length);
  return codeImgs[randomIndex];
}

// 更新验证码图片
function refreshCodeImg() {
  const src = getCodeImgSrc();
  codeImg.setAttribute('src', src);
}

// 初始化验证码图片和刷新按钮
refreshCodeImg();
refreshCodeBtn.addEventListener('click', refreshCodeImg);

// 验证码校验函数
function checkCode() {
  const inputCode = codeInput.value.trim().toLowerCase();
  const imgSrc = codeImg.getAttribute('src');
  const code = imgSrc.substring(imgSrc.lastIndexOf('/') + 1, imgSrc.lastIndexOf('.')).toLowerCase();
  return inputCode === code;
}

// 表单提交事件处理函数
form.addEventListener('submit', function(event) {
  event.preventDefault(); // 阻止表单默认提交行为

  // 校验姓名
  const name = nameInput.value.trim();
  if (!name) {
    alert('请输入姓名');
    nameInput.focus();
    return;
  }

  // 校验电子邮件地址
  const email = emailInput.value.trim();
  if (!email) {
    alert('请输入电子邮件地址');
    emailInput.focus();
    return;
  }

  // 校验验证码
  if (!checkCode()) {
    alert('验证码错误，请重新输入');
    codeInput.value = '';
    codeInput.focus();
    refreshCodeImg();
    return;
  }

  // 表单提交成功
  alert('恭喜您，已成功提交表单');
  form.reset();
});
```

