def sum_of_squares(n):
    """计算1到n个数的平方和"""
    if n == 0:
        return 0
    else:
        return n**2 + sum_of_squares(n-1)

def binary_search(arr, x):
    low = 0
    high = len(arr) - 1
    mid = 0

    while low <= high:
        mid = (high + low) // 2

        if arr[mid] < x:
            low = mid + 1

        elif arr[mid] > x:
            high = mid - 1

        else:
            return mid

    return -1


